

#ifndef GDFGM_DIMS_H
#define GDFGM_DIMS_H

/* Problem dimensions */

#define NX 9                  // number of states
#define NU 3                  // number of inputs

#define NI 10                 // horizon length
#define NP 129                // number of primal variables - (NX+NU)*NI+NX
#define ND 99                 // number of dual variables - (NI+1)*NX        

// Do not reduce space between comment and command above. 
// Left for automatic update of problem dimensions.


/* Macros */

#define max( a, b ) ( ((a) > (b)) ? (a) : (b) )
#define min( a, b ) ( ((a) < (b)) ? (a) : (b) )


#endif















































































































































































